--Tipo de Pick Up (3 es equivalente a uno especial)PICKUP_TYPE = 3--ID del maletin ID_PICKUP = 1210--Identificadores del radarOCEAN_BLUFF_PR = 0BASE_1 = 1GLEEN_PARK_BASE_2 = 2--Vector que guardara las posiciones de las cajitas en un arreglo Bidimensional (Matriz) mas adelantecajitas = {} --declaramos un arraylist--Cantidad de cajitas existentescantidadCajitas = 0--La caja actual se guardara aca (Esto servira para quitarla posteriormente)cajitaActual = nil--Indica si hay una caja puesta. (Esto servira para que no se coloque mas de 1 al tiempo)hayCajaPuesta = false--Indica si ya se inicializo el timeriniciadoTimer = false--Contiene el area del radar actualareaActual = nilmyTimer = nilfunction darReward(thePicker)	local playerName = getPlayerName(thePicker)	local reward = math.random(0,4)	local mensaje = "El jugador "..playerName.." Ha tomado la cajita y ha obtenido: "	if(reward == 0) then		local rewardedMoney = math.random(5000,30000)		givePlayerMoney(thePicker,rewardedMoney)		mensaje = mensaje.."$"..rewardedMoney.." de dinero."	elseif (reward == 1) then		setElementHealth(thePicker, 100)		mensaje = mensaje.."100 de vida."	elseif (reward == 2) then		setPedArmor(thePicker,100)		mensaje = mensaje.."100 de blindaje."	elseif (reward == 3) then		local rewardedRP = math.random(500,3000)		mensaje = mensaje..rewardedRP.." de respeto."	elseif (reward == 4) then		local weaponID = math.random(22,34)		local weaponAmmo = math.random(500,1500)		giveWeapon(thePicker,weaponID,weaponAmmo,true)		mensaje = mensaje..getWeaponNameFromID(weaponID).." con "..weaponAmmo.." de municion."	end	outputChatBox(mensaje.." Felicidades!",getRootElement(),111,111,111,true)		for ElementKey, ElementValue in ipairs ( getAttachedElements (cajitaActual) ) do -- loop trough the attachElements which are attached on the player 		if ( getElementType ( ElementValue ) == "blip" ) then -- if it found a blip element then 			destroyElement( ElementValue )			break		end	end	destroyElement(cajitaActual)	hayCajaPuesta = false	iniciadoTimer = true	outputChatBox("Una cajita caera en 1 minuto!",getRootElement(),111,111,111,true)	myTimer = setTimer(function()		iniciadoTimer = false		if (not hayCajaPuesta) then			ponerCajita()		end		end,60000,0)	destroyElement(areaActual)	areaActual = nil	iniciadoTimer = trueendaddEventHandler("onPickupHit", getRootElement(), darReward)function setACrate()		local index = math.random(0,cantidadCajitas-1)		local x,y,z = cajitas[index][0],cajitas[index][1],cajitas[index][2]		local caja = createPickup(x,y,z,PICKUP_TYPE,ID_PICKUP)		cajitaActual = caja		hayCajaPuesta = true		createBlipAttachedTo(caja,17)		if(cajitas[index][3]==0) then			areaActual = createRadarArea(1523.162109375,-1883.9765625,-510,-400,255,0,0,200)		elseif(cajitas[index][3]==1) then			areaActual = createRadarArea(2744.7333984375,-1378.494140625,150,-250,255,0,0,200)		elseif(cajitas[index][3]==2) then			areaActual = createRadarArea(2060.7666015625,-1270.1201171875,-200,250,255,0,0,200)					end				end--Puestas indica si ya ha sido inicializada la matriz de posicionespuestas = false--setearSpawns: Inicializa las posiciones XYZ de las cajitasfunction setearSpawns()	cantidadCajitas = 15	for i=0,cantidadCajitas do		cajitas[i] = {}	end	--Las posiciones 0,1,2 hacen referencia a las localizaciones XYZ del objeto	--Ocean bluffs (PR)	cajitas[0][0] = 1025.673828125	cajitas[0][1] = -2195.0205078125	cajitas[0][2] = 38.664474487305	cajitas[0][3] = OCEAN_BLUFF_PR	cajitas[1][0] = 1084.302734375	cajitas[1][1] = -2033.857421875	cajitas[1][2] = 68.571136474609	cajitas[1][3] = OCEAN_BLUFF_PR	cajitas[2][0] = 1273.904296875	cajitas[2][1] = -2198.6845703125	cajitas[2][2] = 13.163679122925	cajitas[2][3] = OCEAN_BLUFF_PR	cajitas[3][0] = 1076.7724609375	cajitas[3][1] = -1895.3408203125	cajitas[3][2] = 17.096126556396	cajitas[3][3] = OCEAN_BLUFF_PR	cajitas[4][0] = 1451.66796875	cajitas[4][1] = -1894.0849609375	cajitas[4][2] = 23.15860748291	cajitas[4][3] = OCEAN_BLUFF_PR	--B1	cajitas[5][0] = 2775.4951171875	cajitas[5][1] = -1611.1474609375	cajitas[5][2] = 10.492876052856	cajitas[5][3] = BASE_1	cajitas[6][0] = 2876.6318359375	cajitas[6][1] = -1591.9638671875	cajitas[6][2] = 21.874807357788	cajitas[6][3] = BASE_1	cajitas[7][0] = 2846.40625	cajitas[7][1] = -1528.98828125	cajitas[7][2] = 15.334520339966	cajitas[7][3] = BASE_1	cajitas[8][0] = 2837.236328125	cajitas[8][1] = -1461.75390625	cajitas[8][2] = 40.701019287109	cajitas[8][3] = BASE_1	cajitas[9][0] = 2771.4111328125	cajitas[9][1] = -1400.953125	cajitas[9][2] =	27.178075790405	cajitas[9][3] = BASE_1	--Glenn Park (B2)	cajitas[10][0] = 1970.1181640625	cajitas[10][1] = -1157.5322265625	cajitas[10][2] = 20.521699905396	cajitas[10][3] = GLEEN_PARK_BASE_2	cajitas[11][0] = 1910.453125	cajitas[11][1] = -1108.7548828125	cajitas[11][2] = 33.65625	cajitas[11][3] = GLEEN_PARK_BASE_2	cajitas[12][0] = 2027.0966796875	cajitas[12][1] = -1109.103515625	cajitas[12][2] = 26.203125	cajitas[12][3] = GLEEN_PARK_BASE_2	cajitas[13][0] = 2046.3935546875	cajitas[13][1] = -1036.353515625	cajitas[13][2] = 26.068740844727	cajitas[13][3] = GLEEN_PARK_BASE_2	cajitas[14][0] = 1914.5732421875	cajitas[14][1] = -1066.4033203125	cajitas[14][2] = 24.124662399292	cajitas[14][3] = GLEEN_PARK_BASE_2	puestas = trueendfunction notificarCajita()	outputChatBox("Ha caido una cajita en la zona resaltada. Encuentrala!",getRootElement(),0,255,0,true)endfunction inicializado()	outputChatBox("Se han inicializado las posiciones de las cajitas",getRootElement(),0,255,0,false)endfunction inicializarCajitas()	setearSpawns()	inicializado()endaddCommandHandler("iniCajita",inicializarCajitas)function ponerCajita()	if (puestas == true) then		if(not hayCajaPuesta and not iniciadoTimer) then			setACrate()			notificarCajita()		else			outputChatBox("Hay una caja puesta en este momento. Espera a que sea tomada",getRootElement(),255,0,0,false)		end	else		setearSpawns()		outputChatBox("ADVERTENCIA: No se habia inicializado los spawns, para la proxima inicializar antes de llamar este metodo",getRootElement(),255,0,0,false)		inicializado()		outputChatBox("Vuelve a ejecutar este comando",getRootElement(),255,255,255,false)	endendaddCommandHandler("cajitas",ponerCajita)